mapper.select:
SELECT app_info.id AS id, softwareName, APKName,softwareSize, d1.typeName AS flatformName, a1.categoryName AS categoryLevel1Name, a2.categoryName AS categoryLevel2Name, a3.categoryName AS categoryLevel3Name, d2.typeName AS statusName, downloads,versionNo FROM app_info INNER JOIN data_dictionary AS d1 ON d1.id=app_info.flatformId INNER JOIN data_dictionary AS d2 ON d2.id=app_info.flatformId INNER JOIN app_category AS a1 ON a1.id=app_info.categoryLevel1 INNER JOIN app_category AS a2 ON a2.id=app_info.categoryLevel2 INNER JOIN app_category AS a3 ON a3.id=app_info.categoryLevel3 INNER JOIN app_version ON app_version.id=app_info.versionId
and softwareName=#{softwareName}
and flatformId=#{flatformId}
and categoryLevel1=#{categoryLevel1}
and categoryLevel2=#{categoryLevel2}
and categoryLevel3=#{categoryLevel3}
limit #{index},#{pageCount}

SELECT count(1) FROM app_info


			and	softwareName=#{softwareName}


			and flatformId=#{flatformId}


			and categoryLevel1=#{categoryLevel1}


			and categoryLevel2=#{categoryLevel2}


			and categoryLevel3=#{categoryLevel3}
